import React, { useState } from 'react';
import './CompareModal.css';

import { nutritionFields } from "../../data/DrinkNutritionModel";

function CompareModal({ onClose, nutrient, data }) {
    const [drink1, setDrink1] = useState('');
    const [drink2, setDrink2] = useState('');

    const getDrink = name => data.find(d => d.name === name);

    const drinkOptions = data.map(d => d.name);
    const left = getDrink(drink1);
    const right = getDrink(drink2);

    return (
        <div className="compare-modal">
            <h2>Compare {drink1 && !drink2 ? drink1 : drink2 && !drink1 ? drink2 : "2 Drinks"} by {nutrient}</h2>

            <div className="selectors">
                <select value={drink1} onChange={e => setDrink1(e.target.value)}>
                    <option value="">Select Drink 1</option>
                    {drinkOptions.map(name => (
                        <option key={name} value={name}>{name}</option>
                    ))}
                </select>

                <select value={drink2} onChange={e => setDrink2(e.target.value)}>
                    <option value="">Select Drink 2</option>
                    {drinkOptions.map(name => (
                        <option key={name} value={name}>{name}</option>
                    ))}
                </select>
            </div>

            <div className="compare-grid">
                <div>
                    {left ? (
                        <>
                            <h3>{left.name}</h3>
                            {nutritionFields.map(({ key, label }) => (
                                <p key={key}><strong>{label}:</strong> {left[key]}</p>
                            ))}
                        </>
                    ) : (
                        <p className="placeholder-text">Select Drink 1</p>
                    )}
                </div>

                <div>
                    {right ? (
                        <>
                            <h3>{right.name}</h3>
                            {nutritionFields.map(({ key, label }) => (
                                <p key={key}><strong>{label}:</strong> {right[key]}</p>
                            ))}
                        </>
                    ) : (
                        <p className="placeholder-text">Select Drink 2</p>
                    )}
                </div>
            </div>

            <div style={{ textAlign: 'center', marginTop: '20px' }}>
                <button onClick={onClose}>Close</button>
            </div>
        </div>
    );
}

export default CompareModal;
