import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';
import './TopDrinks.css';

function TopDrinks({ data, nutrient }) {
    const svgRef = useRef();
    const [order, setOrder] = useState('highest');

    useEffect(() => {
        if (!data || data.length === 0) return;

        const top = [...data]
            .sort((a, b) => order === 'highest' ? b[nutrient] - a[nutrient] : a[nutrient] - b[nutrient])
            .slice(0, 10);

        const width = 460;
        const height = 360;
        const margin = { top: 40, right: 20, bottom: 130, left: 50 };

        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove();
        svg.attr('width', width).attr('height', height);

        const x = d3.scaleBand()
            .domain(top.map(d => d.name))
            .range([margin.left, width - margin.right])
            .padding(0.2);

        const y = d3.scaleLinear()
            .domain([0, d3.max(top, d => d[nutrient])])
            .nice()
            .range([height - margin.bottom, margin.top]);

        const g = svg.append('g');

        const tooltip = d3.select('#tooltip');

        g.selectAll('rect')
            .data(top)
            .enter()
            .append('rect')
            .attr('x', d => x(d.name))
            .attr('y', d => y(d[nutrient]))
            .attr('width', x.bandwidth())
            .attr('height', d => y(0) - y(d[nutrient]))
            .attr('fill', '#00704A')
            .on('mouseover', (event, d) => {
                tooltip
                    .style('opacity', 1)
                    .html(`<strong>${d.name}</strong><br/>${nutrient}: ${d[nutrient]}<br/>Sugars: ${d.sugars} g<br/>Caffeine: ${d.caffeine} mg`)
                    .style('left', event.pageX + 10 + 'px')
                    .style('top', event.pageY - 20 + 'px');
            })
            .on('mouseout', () => {
                tooltip.style('opacity', 0);
            });

        g.append('g')
            .attr('transform', `translate(0, ${height - margin.bottom})`)
            .call(d3.axisBottom(x))
            .selectAll('text')
            .attr('transform', 'rotate(-45)')
            .style('text-anchor', 'end')
            .attr('dx', '-0.6em')
            .attr('dy', '0.1em')
            .style('font-size', '11px');

        g.append('g')
            .attr('transform', `translate(${margin.left}, 0)`)
            .call(d3.axisLeft(y));

    }, [data, nutrient, order]);

    return (
        <div className="top-drinks-chart">
            <div className="top-chart-header">
                <h3>Top Drinks by {nutrient}</h3>
                <select value={order} onChange={e => setOrder(e.target.value)}>
                    <option value="highest">Highest</option>
                    <option value="lowest">Lowest</option>
                </select>
            </div>
            <svg ref={svgRef}></svg>
            <div id="tooltip" className="tooltip"></div>
        </div>
    );
}

export default TopDrinks;
