import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import recommendedData from '../../data/RecommendedDrinkPerDay.json';

function RecommendedChart() {
    const ref = useRef();

    useEffect(() => {
        const data = recommendedData.map(d => ({
            group: d.group,
            cups: parseFloat(d.precise_max_cups)
        }));

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = 360;
        const height = 200;
        const margin = { top: 30, right: 20, bottom: 40, left: 50 };

        const x = d3.scaleBand()
            .domain(data.map(d => d.group))
            .range([margin.left, width - margin.right])
            .padding(0.4);

        const y = d3.scaleLinear()
            .domain([0, d3.max(data, d => d.cups)])
            .nice()
            .range([height - margin.bottom, margin.top]);

        svg.attr('width', width).attr('height', height);

        svg.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('x', d => x(d.group))
            .attr('y', d => y(d.cups))
            .attr('width', x.bandwidth())
            .attr('height', d => height - margin.bottom - y(d.cups))
            .attr('fill', '#00704A');

        svg.selectAll('text.label')
            .data(data)
            .enter()
            .append('text')
            .attr('x', d => x(d.group) + x.bandwidth() / 2)
            .attr('y', d => y(d.cups) - 5)
            .attr('text-anchor', 'middle')
            .attr('font-size', '12px')
            .text(d => d.cups.toFixed(2));

        svg.append('g')
            .attr('transform', `translate(0, ${height - margin.bottom})`)
            .call(d3.axisBottom(x));

        svg.append('g')
            .attr('transform', `translate(${margin.left}, 0)`)
            .call(d3.axisLeft(y));

    }, []);

    return (
        <div>
            <h3 style={{ textAlign: 'center' }}>Max Recommended Drinks Per Day</h3>
            <svg ref={ref}></svg>
        </div>
    );
}

export default RecommendedChart;
