// src/components/topdrinks/TopDrinks.js

import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

import './TopDrinks.css';
import { nutritionFields } from '../../data/DrinkNutritionModel';

/**
 * wrap(selection, width)
 *
 * For each selected <text> (tick‐label), split on the first "(" so that:
 *   • Everything before "(" is one line
 *   • The "(" and its contents are a second line
 *
 * We assume exactly two lines max. Any label without "(" remains on one line.
 */
function wrap(selection, width) {
    selection.each(function (d) {
        const textEl = d3.select(this);
        const fullName = d;                // d === the drink‐name string
        const idx = fullName.indexOf('('); // find the first "("

        let line1, line2;
        if (idx >= 0) {
            line1 = fullName.slice(0, idx).trim();
            line2 = fullName.slice(idx).trim();
        } else {
            line1 = fullName;
            line2 = '';
        }

        // Clear whatever D3 originally put here:
        textEl.text('');

        // Append first line (everything before "("):
        textEl
            .append('tspan')
            .attr('x', 0)
            .attr('y', 0)
            .attr('dy', '0')        // explicit “0” so there’s no stray offset
            .text(line1);

        // Append second line only if there was a "("
        if (line2) {
            textEl
                .append('tspan')
                .attr('x', 0)
                .attr('y', 0)
                .attr('dy', '1.2em')   // push this roughly 1.2 × line‐height below line1
                .text(line2);
        }
    });
}

function TopDrinks({ data, nutrient }) {
    const svgRef = useRef();
    const [order, setOrder] = useState('highest');

    useEffect(() => {
        if (!data || data.length === 0) return;

        // 1) Sort and slice top 10 (highest or lowest by nutrient)
        const sorted = [...data]
            .sort((a, b) =>
                order === 'highest'
                    ? b[nutrient] - a[nutrient]
                    : a[nutrient] - b[nutrient]
            )
            .slice(0, 10);

        // 2) Select SVG and clear any previous content
        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove();

        // 3) Figure out how big the parent container is
        const container = svgRef.current.parentNode.getBoundingClientRect();
        const width = container.width;
        const height = container.height;

        // 4) Set margins—only 100px bottom is needed, since each label is a max of 2 lines
        const margin = { top: 40, right: 30, bottom: 100, left: 80 };
        const innerWidth = width - margin.left - margin.right;
        const innerHeight = height - margin.top - margin.bottom;

        svg.attr('width', width).attr('height', height);

        // 5) Build the scales
        const x = d3
            .scaleBand()
            .domain(sorted.map((d) => d.name))
            .range([0, innerWidth])
            .padding(0.2);

        const y = d3
            .scaleLinear()
            .domain([0, d3.max(sorted, (d) => d[nutrient])])
            .nice()
            .range([innerHeight, 0]);

        // 6) Create a group <g> shifted by the margins
        const g = svg
            .append('g')
            .attr('transform', `translate(${margin.left},${margin.top})`);

        // 7) Draw the bars
        g.selectAll('rect')
            .data(sorted)
            .enter()
            .append('rect')
            .attr('x', (d) => x(d.name))
            .attr('y', (d) => y(d[nutrient]))
            .attr('width', x.bandwidth())
            .attr('height', (d) => innerHeight - y(d[nutrient]))
            .attr('fill', '#00704A')
            .on('mouseover', function (event, d) {
                const tooltip = d3.select('#tooltip');
                const content =
                    `<strong>${d.name}</strong><br/>` +
                    nutritionFields
                        .map(({ key, label }) => `${label}: ${d[key]}`)
                        .join('<br/>');

                tooltip
                    .style('opacity', 1)
                    .html(content)
                    .style('left', `${event.offsetX + 10}px`)
                    .style('top', `${event.offsetY + 10}px`);
            })
            .on('mouseout', () => d3.select('#tooltip').style('opacity', 0));

        // 8) Append X‐axis (no rotation this time)
        const xAxisG = g
            .append('g')
            .attr('transform', `translate(0,${innerHeight})`)
            .call(d3.axisBottom(x));

        // 9) Zero out any default dy on each <text> tick, then wrap at "("
        xAxisG
            .selectAll('text')
            .attr('dy', '0')
            .style('text-anchor', 'middle')      
            .style('font-size', '12px')
            .call(wrap, x.bandwidth());

        // 10) Append Y‐axis as before
        g.append('g').call(d3.axisLeft(y));
    }, [data, nutrient, order]);

    return (
        <div className="top-drinks-chart">
            <div className="chart-header">
                <h3>
                    Top Drinks by{' '}
                    {nutrient.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
                </h3>
                <select value={order} onChange={(e) => setOrder(e.target.value)}>
                    <option value="highest">Highest</option>
                    <option value="lowest">Lowest</option>
                </select>
            </div>
            <svg ref={svgRef}></svg>
            <div id="tooltip" className="tooltip" />
        </div>
    );
}

export default TopDrinks;
