import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import './TopDrinks.css';

function TopDrinks({ data, nutrient }) {
    const svgRef = useRef();
    const [order, setOrder] = useState('highest');

    useEffect(() => {
        if (!data || data.length === 0) return;

        const top = [...data]
            .sort((a, b) => order === 'highest' ? b[nutrient] - a[nutrient] : a[nutrient] - b[nutrient])
            .slice(0, 5);

        const width = 500;
        const height = 300;
        const margin = { top: 30, right: 20, bottom: 80, left: 80 };

        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove();
        svg.attr('width', width).attr('height', height);

        const x = d3.scaleBand()
            .domain(top.map(d => d.name))
            .range([0, width - margin.left - margin.right])
            .padding(0.2);

        const y = d3.scaleLinear()
            .domain([0, d3.max(top, d => d[nutrient])])
            .range([height - margin.top - margin.bottom, 0]);

        const g = svg.append('g')
            .attr('transform', `translate(${margin.left}, ${margin.top})`);

        g.selectAll('rect')
            .data(top)
            .enter()
            .append('rect')
            .attr('x', d => x(d.name))
            .attr('y', d => y(d[nutrient]))
            .attr('width', x.bandwidth())
            .attr('height', d => height - margin.top - margin.bottom - y(d[nutrient]))
            .attr('fill', '#00704A')
            .on('mouseover', function (event, d) {
                d3.select('#tooltip')
                    .style('opacity', 1)
                    .html(`
                        <strong>${d.name}</strong><br/>
                        Calories: ${d.calories} g<br/>
                        Total Fat: ${d.total_fat} g<br/>
                        Sugars: ${d.sugars} g<br/>
                        Caffeine: ${d.caffeine} mg<br/>
                        Protein: ${d.protein} g
                    `)
                    .style('left', event.pageX + 10 + 'px')
                    .style('top', event.pageY - 40 + 'px');
            })
            .on('mouseout', () => d3.select('#tooltip').style('opacity', 0));

        g.append('g')
            .attr('transform', `translate(0, ${height - margin.top - margin.bottom})`)
            .call(d3.axisBottom(x))
            .selectAll('text')
            .attr('transform', 'rotate(-40)')
            .style('text-anchor', 'end')
            .style('font-size', '12px');

        g.append('g')
            .call(d3.axisLeft(y).ticks(5));

    }, [data, nutrient, order]);

    return (
        <div className="top-drinks-chart">
            <div className="top-controls">
                <h3 className="chart-title">Top Drinks by {nutrient}</h3>
                <select value={order} onChange={e => setOrder(e.target.value)}>
                    <option value="highest">Highest</option>
                    <option value="lowest">Lowest</option>
                </select>
            </div>
            <svg ref={svgRef}></svg>
            <div id="tooltip" className="tooltip" />
        </div>
    );
}

export default TopDrinks;
