import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import recommendedData from '../data/RecommendedDrinkPerDay.json';

function RecommendedChart() {
    const ref = useRef();

    useEffect(() => {
        const data = recommendedData;

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = 400;
        const height = 200;
        const margin = { top: 30, right: 20, bottom: 60, left: 50 };

        const x = d3.scaleBand()
            .domain(data.map(d => `${d.group} (${d.age})`))
            .range([margin.left, width - margin.right])
            .padding(0.4);

        const y = d3.scaleLinear()
            .domain([0, d3.max(data, d => d.precise_max_cups)])
            .nice()
            .range([height - margin.bottom, margin.top]);

        const g = svg.append('g');

        g.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('x', d => x(`${d.group} (${d.age})`))
            .attr('y', d => y(d.precise_max_cups))
            .attr('width', x.bandwidth())
            .attr('height', d => height - margin.bottom - y(d.precise_max_cups))
            .attr('fill', '#00704A');

        g.selectAll('text.value')
            .data(data)
            .enter()
            .append('text')
            .attr('x', d => x(`${d.group} (${d.age})`) + x.bandwidth() / 2)
            .attr('y', d => y(d.precise_max_cups) - 5)
            .attr('text-anchor', 'middle')
            .attr('font-size', '12px')
            .text(d => d.precise_max_cups.toFixed(2));

        g.append('g')
            .attr('transform', `translate(0,${height - margin.bottom})`)
            .call(d3.axisBottom(x))
            .selectAll('text')
            .attr('transform', 'rotate(-30)')
            .style('text-anchor', 'end')
            .style('font-size', '11px');

        g.append('g')
            .attr('transform', `translate(${margin.left},0)`)
            .call(d3.axisLeft(y).ticks(5));

    }, []);

    return (
        <div className="window">
            <h3 style={{ textAlign: 'center' }}>Max Recommended Drinks Per Day</h3>
            <svg ref={ref} width={400} height={200}></svg>
        </div>
    );
}

export default RecommendedChart;
