import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { formatLabel } from '../../data/DrinkNutritionModel';

function BubbleChart({ nutrient, data }) {
    const ref = useRef();

    useEffect(() => {
        if (!data || data.length === 0) return;

        // Group by category and compute average nutrient
        const categoryMap = {};
        data.forEach(drink => {
            const cat = drink.category;
            if (!categoryMap[cat]) categoryMap[cat] = { total: 0, count: 0 };
            categoryMap[cat].total += drink[nutrient] || 0;
            categoryMap[cat].count += 1;
        });

        const categories = Object.entries(categoryMap).map(([type, val]) => ({
            type,
            avg: val.total / val.count
        }));

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = svg.node().getBoundingClientRect().width;
        const height = svg.node().getBoundingClientRect().height;

        const scale = d3.scaleSqrt()
            .domain([0, d3.max(categories, d => d.avg)])
            .range([15, 45]);

        const simulation = d3.forceSimulation(categories)
            .force('x', d3.forceX(width / 2).strength(0.08))
            .force('y', d3.forceY(height / 2).strength(0.08))
            .force('collide', d3.forceCollide(d => scale(d.avg) + 5))
            .stop();

        for (let i = 0; i < 120; ++i) simulation.tick();

        const g = svg.selectAll('g')
            .data(categories)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x},${d.y})`);

        g.append('circle')
            .attr('r', d => scale(d.avg))
            .attr('fill', 'orange')
            .attr('opacity', 0.85);

        g.append('text')
            .attr('text-anchor', 'middle')
            .attr('dy', '.35em')
            .style('font-size', '12px')
            .style('fill', '#333')
            .text(d => d.type.length > 10 ? d.type.slice(0, 10) + '…' : d.type);

    }, [nutrient, data]);

    return (
        <div className="bubble-chart-container">
            <h3>Average {nutrient.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())} by type of drinks</h3>
            <svg ref={ref}></svg>
        </div>
    );
}

export default BubbleChart;
