import React, { useState } from 'react';

function TopDrinks({ nutrient, onDrinkSelect, data }) {
    const [order, setOrder] = useState('desc');

    if (!data || data.length === 0) return <div>Loading...</div>;

    const sorted = [...data].sort((a, b) =>
        order === 'desc' ? b[nutrient] - a[nutrient] : a[nutrient] - b[nutrient]
    ).slice(0, 5);

    return (
        <div className="top-drinks">
            <div className="top-header">
                <h3>Top Drinks by {nutrient}</h3>
                <select value={order} onChange={e => setOrder(e.target.value)}>
                    <option value="desc">Highest</option>
                    <option value="asc">Lowest</option>
                </select>
            </div>

            <ul className="drink-list">
                {sorted.map(drink => (
                    <li
                        key={drink.name}
                        className="drink-item"
                        onClick={() => onDrinkSelect(drink.name)}
                    >
                        <span className="drink-name">{drink.name}</span>
                        <span className="drink-value">{drink[nutrient]} g</span>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TopDrinks;
