import "./NutrientSelector.css"

function NutrientSelector({ nutrient, setNutrient }) {
    const options = [
        'calories', 'total_fat', 'trans_fat', 'saturated_fat',
        'sodium', 'carbohydrates', 'cholesterol', 'fiber',
        'sugars', 'protein', 'vitaminA', 'vitaminC', 'calcium',
        'iron', 'caffeine'
    ];

    return (
        <div className="nutrient-selector">
            {options.map((opt) => (
                <label key={opt} className="nutrient-option">
                    <input
                        type="radio"
                        value={opt}
                        checked={nutrient === opt}
                        onChange={(e) => setNutrient(e.target.value)}
                    />
                    {opt.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
                </label>
            ))}
        </div>
    );
}

export default NutrientSelector;
