import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { formatLabel } from '../../data/DrinkNutritionModel';
import './BubbleChart.css';

function BubbleChart({ data, nutrient, category }) {
    const ref = useRef();

    useEffect(() => {
        if (!data || data.length === 0) return;

        let bubblesData;

        if (category === 'drinks') {
            const grouped = d3.groups(data, d => d.category);
            bubblesData = grouped
                .map(([cat, items]) => {
                    const valid = items.filter(
                        d => d[nutrient] !== undefined && !isNaN(d[nutrient])
                    );
                    const avg = d3.mean(valid, d => +d[nutrient]);
                    return { label: cat, value: avg };
                })
                .filter(d => !isNaN(d.value));
        } else {
            const sortedFoods = [...data]
                .filter(d => d[nutrient] !== undefined && !isNaN(d[nutrient]))
                .sort((a, b) => b[nutrient] - a[nutrient])
                .slice(0, 10);

            bubblesData = sortedFoods.map(d => ({
                label: d.name,
                value: +d[nutrient],
            }));
        }

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const container = svg.node().parentElement;
        const width = container.clientWidth;
        const height = container.clientHeight; // now 450px from CSS

        const scale = d3
            .scaleSqrt()
            .domain([0, d3.max(bubblesData, d => d.value)])
            .range([30, 70]);

        const simulation = d3
            .forceSimulation(bubblesData)
            .force('x', d3.forceX(width / 2).strength(0.05))
            // shift center slightly upward to avoid bottom clipping
            .force('y', d3.forceY(height * 0.45).strength(0.1))
            .force('collide', d3.forceCollide(d => scale(d.value) + 4))
            .stop();

        for (let i = 0; i < 100; i++) simulation.tick();

        svg
            .attr('viewBox', `0 0 ${width} ${height}`)
            .attr('preserveAspectRatio', 'xMidYMid meet');

        const g = svg
            .selectAll('g')
            .data(bubblesData)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x},${d.y})`);

        g.append('circle')
            .attr('r', d => scale(d.value))
            .attr('fill', '#00704A')
            .attr('stroke', '#e76f51')
            .attr('stroke-width', 1)
            .attr('opacity', 0.85);

        g.append('text')
            .style('text-anchor', 'middle')
            .style('font-size', d => `${Math.min(scale(d.value) / 3, 12)}px`)
            .style('fill', 'white')
            .style('pointer-events', 'none')
            .selectAll('tspan')
            .data(d => {
                const words = d.label.split(' ');
                const lines = [];
                for (let i = 0; i < words.length; i += 2) {
                    lines.push(words.slice(i, i + 2).join(' '));
                }
                return lines;
            })
            .enter()
            .append('tspan')
            .attr('x', 0)
            .attr('dy', function (_, i, nodes) {
                const totalLines = nodes.length;
                const lineHeight = 1.2;
                const firstDy = -((totalLines - 1) / 2) * lineHeight;
                return i === 0 ? `${firstDy}em` : `${lineHeight}em`;
            })
            .text(d => d);
    }, [data, nutrient, category]);

    const headerText =
        category === 'drinks'
            ? `Average ${formatLabel(nutrient)} by Type of Drinks`
            : `Top 10 Food Items by ${formatLabel(nutrient)}`;

    return (
        <div className="bubble-chart-container">
            <h3>{headerText}</h3>
            <svg ref={ref}></svg>
        </div>
    );
}

export default BubbleChart;
