import React from 'react';
import './CompareModal.css'; // optional separate style

function CompareModal({ onClose, nutrient, leftDrink, rightDrink, data }) {
    const nutrientKeys = [
        'calories', 'total_fat', 'trans_fat', 'saturated_fat', 'sodium',
        'carbohydrates', 'cholesterol', 'fiber', 'sugars', 'protein',
        'vitaminA', 'vitaminC', 'calcium', 'iron', 'caffeine'
    ];

    const left = data.find(d => d.name === leftDrink);
    const right = data.find(d => d.name === rightDrink);

    return (
        <div className="compare-modal">
            <h2>Compare 2 Drinks by {nutrient}</h2>

            <div className="compare-grid">
                <div>
                    <h3>{left.name}</h3>
                    {nutrientKeys.map(key => (
                        <p key={key}>
                            <strong>{key.replace(/_/g, ' ')}:</strong> {left[key]} g
                        </p>
                    ))}
                </div>

                <div>
                    <h3>{right.name}</h3>
                    {nutrientKeys.map(key => (
                        <p key={key}>
                            <strong>{key.replace(/_/g, ' ')}:</strong> {right[key]} g
                        </p>
                    ))}
                </div>
            </div>

            <div style={{ textAlign: 'center', marginTop: '20px' }}>
                <button onClick={onClose}>Close</button>
            </div>
        </div>
    );
}

export default CompareModal;
