import React, { useState } from 'react';

function TopDrinks({ nutrient, onDrinkSelect, data }) {
    const [order, setOrder] = useState('highest'); // 'highest' or 'lowest'

    if (!data || data.length === 0) return null;

    const sorted = [...data].sort((a, b) =>
        order === 'highest' ? b[nutrient] - a[nutrient] : a[nutrient] - b[nutrient]
    );

    const topDrinks = sorted.slice(0, 5);

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h3 style={{ margin: 0 }}>Top Drinks by {nutrient}</h3>
                <select value={order} onChange={e => setOrder(e.target.value)}>
                    <option value="highest">Highest</option>
                    <option value="lowest">Lowest</option>
                </select>
            </div>

            <ul style={{ marginTop: '10px', paddingLeft: '20px' }}>
                {topDrinks.map((drink, idx) => (
                    <li key={idx} style={{ cursor: 'pointer' }} onClick={() => onDrinkSelect?.(drink.name)}>
                        <strong>{drink.name}</strong> â€“ {drink[nutrient]} g
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TopDrinks;
