function DrinkInfo({ drinkName, nutrient }) {
    if (!drinkName) return null;

    // Replace this with real lookup later
    const drinkData = {
        'Caffè Latte': { calories: 190, sugars: 17, caffeine: 75 },
        'Caffè Mocha': { calories: 260, sugars: 33, caffeine: 90 },
        'White Chocolate Mocha': { calories: 400, sugars: 53, caffeine: 75 },
        'Caramel Macchiato': { calories: 250, sugars: 30, caffeine: 150 },
        'Java Chip Frappuccino': { calories: 420, sugars: 60, caffeine: 95 },
    };

    const data = drinkData[drinkName];

    if (!data) return <p>Loading...</p>;

    return (
        <div className="drink-info">
            <h3>{drinkName} ▼</h3>
            <p>
                {nutrient.replace(/_/g, ' ')}: <strong>{data[nutrient]}</strong>{' '}
                {nutrient === 'caffeine' ? 'mg' : 'g'}
            </p>
        </div>
    );
}

export default DrinkInfo;
