import React, { useState } from 'react';
import './RecommendedItems.css';

function RecommendedItems({ data, nutrient }) {
    const [hoveredIndex, setHoveredIndex] = useState(null);
    if (!data || data.length === 0) return null;

    const label = nutrient
        .replace(/_/g, ' ')
        .replace(/\b\w/g, (c) => c.toUpperCase());

    const matching =
        nutrient === 'vitaminA' || nutrient === 'vitaminC'
            ? data.filter((d) =>
                d.factor.toLowerCase().includes('vitamin (a & c)')
            )
            : data.filter((d) =>
                d.factor.toLowerCase().includes(nutrient.toLowerCase())
            );

    let listToShow;
    if (matching.length > 0) {
        listToShow = matching;
    } else {
        const lowestCal = data.reduce((prev, curr) =>
            curr.calories < prev.calories ? curr : prev
        );
        listToShow = [lowestCal];
    }

    const factorText = listToShow[0].factor;
    const drinkWord = listToShow.length > 1 ? 'Drinks' : 'Drink';

    return (
        <div className="drink-info">
            <h3>
                Recommended {factorText} {drinkWord}
            </h3>

            <ul className="drink-list">
                {listToShow.map((item, idx) => (
                    <li
                        key={item.name}
                        className="drink-item"
                        onMouseEnter={() => setHoveredIndex(idx)}
                        onMouseLeave={() => setHoveredIndex(null)}
                    >
                        <span className="drink-name">{item.name}</span>

                        {hoveredIndex === idx && (
                            <div className="tooltip-content">
                                <div className="tooltip-title">{item.name}</div>

                                {Object.entries(item).map(([key, val]) => {
                                    if (key === 'name') return null;
                                    if (key === 'factor') return null;

                                    // Convert camelCase or snake_case → Title Case:
                                    const labelKey = key
                                        .replace(/_/g, ' ')
                                        .replace(/\b\w/g, (l) => l.toUpperCase());

                                    if (typeof val === 'number' || !isNaN(val)) {
                                        let unit = '';
                                        if (key === 'calories') unit = '';
                                        else if (key.toLowerCase().includes('fat')) unit = ' g';
                                        else if (
                                            ['sodium', 'cholesterol'].includes(key.toLowerCase())
                                        )
                                            unit = ' mg';
                                        else if (['caffeine'].includes(key.toLowerCase()))
                                            unit = ' mg';
                                        else if (
                                            ['vitamina', 'vitaminc', 'calcium', 'iron'].includes(
                                                key.toLowerCase()
                                            )
                                        )
                                            unit = ' %';
                                        else if (key === 'fiber' || key === 'protein' || key === 'sugars' || key === 'carbohydrates') unit = ' g';

                                        return (
                                            <div key={key} className="tooltip-row">
                                                {labelKey}: {val}
                                                {unit}
                                            </div>
                                        );
                                    }

                                    if (typeof val === 'string' && val.trim() !== '') {
                                        return (
                                            <div key={key} className="tooltip-row">
                                                {labelKey}: <em>{val}</em>
                                            </div>
                                        );
                                    }

                                    return null;
                                })}

                                <div className="tooltip-row">
                                    Factor: <span>{item.factor}</span>
                                </div>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default RecommendedItems;
