import { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function RecommendedChart({ category }) {
    const ref = useRef();

    useEffect(() => {
        const data = [
            { ageGroup: '12–18', max: 1.24 },
            { ageGroup: '19–30', max: 3.76 },
        ];

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = 300;
        const height = 150;
        const margin = { top: 20, right: 20, bottom: 40, left: 40 };

        const x = d3.scaleBand()
            .domain(data.map(d => d.ageGroup))
            .range([margin.left, width - margin.right])
            .padding(0.4);

        const y = d3.scaleLinear()
            .domain([0, d3.max(data, d => d.max)])
            .range([height - margin.bottom, margin.top]);

        svg.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('x', d => x(d.ageGroup))
            .attr('y', d => y(d.max))
            .attr('width', x.bandwidth())
            .attr('height', d => height - margin.bottom - y(d.max))
            .attr('fill', 'teal');

        svg.selectAll('text.label')
            .data(data)
            .enter()
            .append('text')
            .attr('x', d => x(d.ageGroup) + x.bandwidth() / 2)
            .attr('y', d => y(d.max) - 5)
            .attr('text-anchor', 'middle')
            .text(d => d.max.toFixed(2));

    }, [category]);

    return (
        <div>
            <h3>Max Recommended Drinks Per Day</h3>
            <svg ref={ref} width={300} height={150}></svg>
        </div>
    );
}

export default RecommendedChart;
