function NutrientSelector({ nutrient, setNutrient }) {
    const options = [
        'calories', 'total_fat', 'trans_fat', 'saturated_fat',
        'sodium', 'carbohydrates', 'cholesterol', 'fiber',
        'sugars', 'protein', 'vitaminA', 'vitaminC', 'calcium',
        'iron', 'caffeine'
    ];

    return (
        <select value={nutrient} onChange={(e) => setNutrient(e.target.value)}>
            {options.map((opt) => (
                <option key={opt} value={opt}>
                    {opt.replace(/_/g, ' ').replace(/\b\w/g, (c) => c.toUpperCase())}
                </option>
            ))}
        </select>
    );
}

export default NutrientSelector;
