import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import recommendedData from '../../data/RecommendedDrinkPerDay.json';
import './RecommendedChart.css';

function RecommendedChart() {
    const ref = useRef();

    useEffect(() => {
        const data = recommendedData;

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = 400;
        const height = 200;
        const margin = { top: 20, right: 20, bottom: 40, left: 40 };

        const x = d3.scaleBand()
            .domain(data.map(d => d.ageGroup))
            .range([margin.left, width - margin.right])
            .padding(0.4);

        const y = d3.scaleLinear()
            .domain([0, d3.max(data, d => d.max || 0)])
            .nice()
            .range([height - margin.bottom, margin.top]);

        svg.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('x', d => x(d.ageGroup))
            .attr('y', d => y(d.max || 0))
            .attr('width', x.bandwidth())
            .attr('height', d => height - margin.bottom - y(d.max || 0))
            .attr('fill', '#00704A');

        svg.selectAll('text.label')
            .data(data)
            .enter()
            .append('text')
            .attr('x', d => x(d.ageGroup) + x.bandwidth() / 2)
            .attr('y', d => y(d.max || 0) - 5)
            .attr('text-anchor', 'middle')
            .text(d => d.max ? d.max.toFixed(2) : 'N/A');

        svg.append('g')
            .attr('transform', `translate(0, ${height - margin.bottom})`)
            .call(d3.axisBottom(x));

        svg.append('g')
            .attr('transform', `translate(${margin.left}, 0)`)
            .call(d3.axisLeft(y));
    }, []);

    return (
        <div className="recommended-chart-container">
            <h3>Max Recommended Drinks Per Day</h3>
            <svg ref={ref} width={400} height={200}></svg>
        </div>
    );
}

export default RecommendedChart;
