import { useEffect, useState } from 'react';
import * as d3 from 'd3';

import NutrientSelector from './components/nutrientselector/NutrientSelector';
import RecommendedChart from './components/RecommendedChart';
import TopDrinks from './components/TopDrinks';
import DrinkInfo from './components/DrinkInfo';
import AvgNutrientChart from './components/AvgNutrientChart';
import CompareModal from './components/comparemodal/CompareModal';

import './styles/App.css';

function App() {
    const [nutrient, setNutrient] = useState('calories');
    const [selectedDrink, setSelectedDrink] = useState(null);
    const [compareOpen, setCompareOpen] = useState(false);
    const [allDrinksData, setAllDrinksData] = useState([]);

    useEffect(() => {
        d3.csv('/data/StarbucksDrinks.csv').then(data => {
            const formatted = data.map(d => ({
                ...d,
                calories: +d.calories,
                total_fat: +d.total_fat,
                trans_fat: +d.trans_fat,
                saturated_fat: +d.saturated_fat,
                sodium: +d.sodium,
                carbohydrates: +d.carbohydrates,
                cholesterol: +d.cholesterol,
                fiber: +d.fiber,
                sugars: +d.sugars,
                protein: +d.protein,
                vitaminA: +d.vitaminA,
                vitaminC: +d.vitaminC,
                calcium: +d.calcium,
                iron: +d.iron,
                caffeine: +d.caffeine
            }));
            setAllDrinksData(formatted);
        });
    }, []);

    return (
        <div className="App">
            <div className="header">
                <h1>Starbucks Nutrition</h1>
            </div>

            <NutrientSelector nutrient={nutrient} setNutrient={setNutrient} />

            <div className="toolbar">
                <button onClick={() => setCompareOpen(true)}>Compare Drinks</button>
            </div>

            <div className="dashboard">
                <div className="dashboard-top">
                    <div className="window">
                        <RecommendedChart />
                    </div>
                    <div className="window">
                        <TopDrinks nutrient={nutrient} onDrinkSelect={setSelectedDrink} />
                    </div>
                </div>

                <div className="dashboard-bottom">
                    <div className="window">
                        <DrinkInfo drinkName={selectedDrink} nutrient={nutrient} />
                    </div>
                    <div className="window">
                        <AvgNutrientChart onBubbleClick={setSelectedDrink} nutrient={nutrient} />
                    </div>
                </div>
            </div>


            {compareOpen && (
                <div className="overlay">
                    <div className="compare-modal">
                        <CompareModal
                            onClose={() => setCompareOpen(false)}
                            nutrient={nutrient}
                            data={allDrinksData}
                        />
                    </div>
                </div>
            )}

            <footer className="footer">
                Note: Seasonal drinks are not included in this dashboard.
            </footer>
        </div>
    );
}

export default App;
