import Modal from 'react-modal';
import { useState } from 'react';

function CompareModal({ onClose }) {
    const [drink1, setDrink1] = useState('');
    const [drink2, setDrink2] = useState('');

    return (
        <Modal isOpen={true} onRequestClose={onClose} contentLabel="Compare Drinks">
            <h2>Compare the Nutrition Content of 2 Drinks</h2>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <div>
                    <select value={drink1} onChange={e => setDrink1(e.target.value)}>
                        <option value="">-- select --</option>
                        <option value="Caffè Latte">Caffè Latte</option>
                        <option value="Java Chip">Java Chip</option>
                    </select>
                    <p>Calories: ...</p>
                    <p>Total Fat: ...</p>
                </div>

                <div>
                    <select value={drink2} onChange={e => setDrink2(e.target.value)}>
                        <option value="">-- select --</option>
                        <option value="Caffè Latte">Caffè Latte</option>
                        <option value="Java Chip">Java Chip</option>
                    </select>
                    <p>Calories: ...</p>
                    <p>Total Fat: ...</p>
                </div>
            </div>
            <button onClick={onClose}>Close</button>
        </Modal>
    );
}

export default CompareModal;
