import { useState } from 'react';

import CategorySelector from './components/CategorySelector';
import RecommendedChart from './components/RecommendedChart';
import TopDrinks from './components/TopDrinks';
import DrinkInfo from './components/DrinkInfo';
import AvgCaffeineChart from './components/AvgCaffeineChart';
import CompareModal from './components/CompareModal';

import './styles/App.css';

function App() {
  const [category, setCategory] = useState('Drink');
  const [selectedDrink, setSelectedDrink] = useState(null);
  const [compareOpen, setCompareOpen] = useState(false);

  return (
    <div className="App">
      <h1>Starbucks Nutrition</h1>
      <CategorySelector category={category} setCategory={setCategory} />

      <div className="dashboard">
        <RecommendedChart category={category} />
        <TopDrinks category={category} onDrinkSelect={setSelectedDrink} />
        <DrinkInfo drinkName={selectedDrink} />
        <AvgCaffeineChart category={category} onBubbleClick={setSelectedDrink} />
        <button onClick={() => setCompareOpen(true)}>Compare Drinks</button>
      </div>

      {compareOpen && <CompareModal onClose={() => setCompareOpen(false)} />}
    </div>
  );
}

export default App;
