import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { formatLabel } from '../../data/DrinkNutritionModel';

function BubbleChart({ data, nutrient }) {
    const ref = useRef();

    useEffect(() => {
        if (!data || data.length === 0) return;

        // Group by category and compute average of selected nutrient
        const grouped = d3.groups(data, d => d.category)
            .map(([category, items]) => ({
                category,
                avg: d3.mean(items, d => +d[nutrient])
            }))
            .filter(d => d.avg !== undefined && !isNaN(d.avg));

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = svg.node().parentNode.clientWidth;
        const height = 300;

        svg.attr('width', width).attr('height', height);

        const pack = d3.pack()
            .size([width, height])
            .padding(10);

        const root = d3.hierarchy({ children: grouped })
            .sum(d => d.avg);

        const nodes = pack(root).leaves();

        const g = svg.append('g');

        const node = g.selectAll('g')
            .data(nodes)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x},${d.y})`);

        node.append('circle')
            .attr('r', d => d.r)
            .attr('fill', '#f4a261')
            .attr('opacity', 0.9);

        node.append('text')
            .attr('text-anchor', 'middle')
            .style('font-size', d => Math.min(14, d.r / 3) + 'px')
            .style('fill', '#333')
            .text(d => d.data.category.length > 15 ? d.data.category.slice(0, 13) + '…' : d.data.category);

    }, [data, nutrient]);

    return (
        <div className="bubble-chart-container">
            <h3>Average {formatLabel(nutrient)} by Type of Drinks</h3>
            <svg ref={ref} />
        </div>
    );
}

export default BubbleChart;
