import recommendations from '../data/HealthyDrinkRecommendations.json';

function RecommendedItems({ drinkName, nutrient }) {
    if (!drinkName) return null;

    const selected = recommendations.find(d => d.name === drinkName);
    if (!selected) return <p>Loading...</p>;

    const label = nutrient.replace(/_/g, ' ').replace(/\b\w/g, c => c.toUpperCase());
    const unit = ['caffeine'].includes(nutrient) ? 'mg' :
        ['calcium', 'iron', 'vitaminA', 'vitaminC'].includes(nutrient) ? '%' : 'g';

    return (
        <div className="drink-info">
            <h3>{drinkName} â–¼</h3>
            <p>
                {label}: <strong>{selected[nutrient]}</strong> {unit}
            </p>
        </div>
    );
}

export default RecommendedItems;
