import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { formatLabel } from '../../data/DrinkNutritionModel';

function BubbleChart({ nutrient }) {
    const ref = useRef();

    useEffect(() => {
        const width = 400;
        const height = 300;

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        // Group drinks by type and compute average nutrient value
        import('../../data/StarbucksDrinks.json').then((module) => {
            const drinks = module.default;

            const grouped = d3.group(drinks, d => d.type);
            const data = Array.from(grouped, ([type, items]) => ({
                type,
                avg: d3.mean(items, d => d[nutrient])
            })).filter(d => d.avg != null);

            const scale = d3.scaleSqrt()
                .domain([0, d3.max(data, d => d.avg)])
                .range([20, 45]);

            const simulation = d3.forceSimulation(data)
                .force('x', d3.forceX(width / 2).strength(0.05))
                .force('y', d3.forceY(height / 2).strength(0.05))
                .force('collide', d3.forceCollide(d => scale(d.avg) + 4))
                .stop();

            for (let i = 0; i < 120; ++i) simulation.tick();

            const g = svg.selectAll('g')
                .data(data)
                .enter()
                .append('g')
                .attr('transform', d => `translate(${d.x},${d.y})`);

            g.append('circle')
                .attr('r', d => scale(d.avg))
                .attr('fill', 'orange')
                .attr('opacity', 0.8);

            g.append('text')
                .attr('text-anchor', 'middle')
                .attr('dy', '.35em')
                .style('font-size', '12px')
                .style('fill', '#333')
                .text(d => d.type.length > 12 ? d.type.slice(0, 10) + '…' : d.type);
        });
    }, [nutrient]);

    return (
        <div>
            <h3 style={{ textAlign: 'center', marginBottom: '12px' }}>
                Average {formatLabel(nutrient)} by Type of Drinks
            </h3>
            <svg ref={ref} width="100%" height="300px"></svg>
        </div>
    );
}

export default BubbleChart;
