import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { formatLabel } from '../../data/DrinkNutritionModel';

function BubbleChart({ data, nutrient }) {
    const ref = useRef();

    useEffect(() => {
        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        if (!data || data.length === 0) return;

        const grouped = d3.group(
            data.filter(d => d.type && typeof d[nutrient] === 'number'),
            d => d.type
        );

        const summary = Array.from(grouped, ([type, values]) => ({
            type,
            avg: d3.mean(values, d => d[nutrient])
        }));

        const width = svg.node().getBoundingClientRect().width;
        const height = 250;

        const scale = d3.scaleSqrt()
            .domain([0, d3.max(summary, d => d.avg)])
            .range([20, 50]);

        const simulation = d3.forceSimulation(summary)
            .force('x', d3.forceX(width / 2).strength(0.05))
            .force('y', d3.forceY(height / 2).strength(0.05))
            .force('collide', d3.forceCollide(d => scale(d.avg) + 4))
            .stop();

        for (let i = 0; i < 120; ++i) simulation.tick();

        const g = svg.selectAll('g')
            .data(summary)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x},${d.y})`);

        g.append('circle')
            .attr('r', d => scale(d.avg))
            .attr('fill', 'orange')
            .attr('opacity', 0.8);

        g.append('text')
            .attr('text-anchor', 'middle')
            .attr('dy', '.35em')
            .style('font-size', '12px')
            .style('fill', '#333')
            .text(d => d.type.length > 12 ? d.type.slice(0, 10) + '…' : d.type);
    }, [data, nutrient]);

    return (
        <div>
            <h3 style={{ textAlign: 'center', marginBottom: '12px' }}>
                Average {formatLabel(nutrient)} by Type of Drinks
            </h3>
            <svg ref={ref} style={{ width: '100%', height: '250px' }} />
        </div>
    );
}

export default BubbleChart;
