import {useEffect, useState} from "react";

function TopDrinks({ nutrient, onDrinkSelect }) {
    const [topDrinks, setTopDrinks] = useState([]);

    useEffect(() => {
        const mockData = [
            { Beverage: 'Java Chip Frappuccino', calories: 420, sugars: 60, caffeine: 95 },
            { Beverage: 'White Chocolate Mocha', calories: 400, sugars: 53, caffeine: 75 },
            { Beverage: 'Caramel Macchiato', calories: 250, sugars: 30, caffeine: 150 },
            { Beverage: 'Caffè Mocha', calories: 260, sugars: 33, caffeine: 90 },
            { Beverage: 'Caffè Latte', calories: 190, sugars: 17, caffeine: 75 },
        ];

        const sorted = mockData
            .filter(d => d[nutrient] !== undefined)
            .sort((a, b) => b[nutrient] - a[nutrient])
            .slice(0, 5);

        setTopDrinks(sorted);
    }, [nutrient]);

    return (
        <div className="top-drinks">
            <h3>Top Drinks by {nutrient.replace(/_/g, ' ')}</h3>
            <div className="drink-cards">
                {topDrinks.map((drink, i) => (
                    <div
                        key={i}
                        className="drink-card"
                        onClick={() => onDrinkSelect(drink.Beverage)}
                    >
                        <strong>{drink.Beverage}</strong>
                        <p>{drink[nutrient]} {nutrient === 'caffeine' ? 'mg' : 'g'}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}
