import { useState } from 'react';

import NutrientSelector from './components/nutrientselector/NutrientSelector';
import RecommendedChart from './components/recommendedchart/RecommendedChart';
import TopDrinks from './components/topdrinks/TopDrinks';
import RecommendedItems from './components/recommendeditems/RecommendedItems';
import BubbleChart from './components/bubblechart/BubbleChart';
import CompareModal from './components/comparemodal/CompareModal';

import './styles/App.css';

import drinksData from './data/StarbucksDrinks.json';
import drinkRecommendation from './data/HealthyDrinkRecommendations.json'

function App() {
    const allDrinksData = drinksData;
    const [nutrient, setNutrient] = useState('calories');
    const [compareOpen, setCompareOpen] = useState(false);

    return (
        <div className="App">
            <div className="header">
                <h1>Starbucks Nutrition</h1>
            </div>

            <NutrientSelector nutrient={nutrient} setNutrient={setNutrient} />

            <div className="toolbar">
                <button onClick={() => setCompareOpen(true)}>Compare Drinks</button>
            </div>

            <div className="dashboard">
                <div className="dashboard-top">
                    <div className="window">
                        <RecommendedChart />
                    </div>
                    <div className="window">
                        <TopItems data={allDrinksData} nutrient={nutrient} />
                    </div>
                </div>

                <div className="dashboard-bottom">
                    <div className="window">
                        <BubbleChart data={allDrinksData} nutrient={nutrient} />
                    </div>
                    <div className="window">
                        <RecommendedItems data={drinkRecommendation} nutrient={nutrient} />
                    </div>
                </div>
            </div>


            {compareOpen && (
                <div className="compare-overlay">
                    <CompareModal
                        onClose={() => setCompareOpen(false)}
                        nutrient={nutrient}
                        data={allDrinksData}
                    />
                </div>
            )}

            <footer className="footer">
                Note: Seasonal drinks are not included in this dashboard. All drinks listed are in grande size.
            </footer>
        </div>
    );
}

export default App;
