import { useState } from 'react';
import Modal from 'react-modal';

function CompareModal({ onClose, nutrient }) {
    const [drink1, setDrink1] = useState('Caffè Latte');
    const [drink2, setDrink2] = useState('Java Chip Frappuccino');

    const drinkData = {
        'Caffè Latte': { calories: 190, sugars: 17, caffeine: 75 },
        'Java Chip Frappuccino': { calories: 420, sugars: 60, caffeine: 95 },
    };

    return (
        <Modal isOpen={true} onRequestClose={onClose}>
            <h2>Compare 2 Drinks by {nutrient.replace(/_/g, ' ')}</h2>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                {[drink1, drink2].map((drink, index) => (
                    <div key={index}>
                        <select
                            value={drink}
                            onChange={(e) =>
                                index === 0 ? setDrink1(e.target.value) : setDrink2(e.target.value)
                            }
                        >
                            {Object.keys(drinkData).map((d) => (
                                <option key={d} value={d}>
                                    {d}
                                </option>
                            ))}
                        </select>
                        <p>
                            {nutrient.replace(/_/g, ' ')}:{' '}
                            {drinkData[drink] ? drinkData[drink][nutrient] : '...'}{' '}
                            {nutrient === 'caffeine' ? 'mg' : 'g'}
                        </p>
                    </div>
                ))}
            </div>
            <button onClick={onClose}>Close</button>
        </Modal>
    );
}

export default CompareModal;
