import { useState } from 'react';

import NutrientSelector from './components/nutrientselector/NutrientSelector';
import RecommendedChart from './components/RecommendedChart';
import TopDrinks from './components/TopDrinks';
import DrinkInfo from './components/DrinkInfo';
import AvgNutrientChart from './components/AvgNutrientChart';
import CompareModal from './components/CompareModal';

import './styles/App.css';

function App() {
    const [nutrient, setNutrient] = useState('calories');
    const [selectedDrink, setSelectedDrink] = useState(null);
    const [compareOpen, setCompareOpen] = useState(false);

    return (
        <div className="App">
            <div className="header">
                <h1>Starbucks Nutrition</h1>
            </div>

            <NutrientSelector nutrient={nutrient} setNutrient={setNutrient} />

            <div className="toolbar">
                <button onClick={() => setCompareOpen(true)}>Compare Drinks</button>
            </div>

            <div className="dashboard">
                <div className="dashboard-top">
                    <div className="window">
                        <RecommendedChart />
                    </div>
                    <div className="window">
                        <TopDrinks nutrient={nutrient} onDrinkSelect={setSelectedDrink} />
                    </div>
                </div>

                <div className="dashboard-bottom">
                    <div className="window">
                        <DrinkInfo drinkName={selectedDrink} nutrient={nutrient} />
                    </div>
                    <div className="window">
                        <AvgNutrientChart onBubbleClick={setSelectedDrink} nutrient={nutrient} />
                    </div>
                </div>
            </div>


            {compareOpen && <CompareModal onClose={() => setCompareOpen(false)} nutrient={nutrient} />}
        </div>
    );
}

export default App;
