import { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function AvgCaffeineChart({ category, onBubbleClick }) {
    const ref = useRef();

    useEffect(() => {
        const dummyData = [
            { type: 'Espresso', avg: 90 },
            { type: 'Frappuccino', avg: 60 },
            { type: 'Tea', avg: 40 },
        ];

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = 300;
        const height = 200;

        const scale = d3.scaleSqrt().domain([0, 100]).range([10, 50]);

        const simulation = d3.forceSimulation(dummyData)
            .force('x', d3.forceX(width / 2).strength(0.05))
            .force('y', d3.forceY(height / 2).strength(0.05))
            .force('collide', d3.forceCollide(d => scale(d.avg) + 4))
            .stop();

        for (let i = 0; i < 100; ++i) simulation.tick();

        const nodes = svg.selectAll('g')
            .data(dummyData)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x}, ${d.y})`)
            .on('click', d => onBubbleClick(d.type));

        nodes.append('circle')
            .attr('r', d => scale(d.avg))
            .attr('fill', 'orange');

        nodes.append('text')
            .attr('text-anchor', 'middle')
            .attr('dy', '.35em')
            .text(d => d.type);

    }, [category]);

    return (
        <div>
            <h3>Average Caffeine by Type</h3>
            <svg ref={ref} width={300} height={200}></svg>
        </div>
    );
}

export default AvgCaffeineChart;
