import React, { useState } from 'react';

function RecommendedItems({ data, nutrient }) {
    const [hoveredIndex, setHoveredIndex] = useState(null);

    if (!data || data.length === 0) return null;

    const label = nutrient
        .replace(/_/g, ' ')
        .replace(/\b\w/g, (c) => c.toUpperCase());

    const unitMap = {
        caffeine: 'mg',
        vitaminA: '%',
        vitaminC: '%',
        calcium: '%',
        iron: '%',
        calories: '',
    };
    const unit = unitMap[nutrient] || 'g';

   const matching = data.filter((d) =>
        d.factor.toLowerCase().includes(nutrient.toLowerCase())
    );

    if (matching.length === 0) {
        return (
            <div className="drink-info">
                <h3>No recommendations found for {label}</h3>
            </div>
        );
    }

    return (
        <div className="drink-info">
            <h3>Recommended {label} Drinks</h3>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                {matching.map((item, idx) => {
                    const value = item[nutrient];

                    return (
                        <li
                            key={item.name}
                            style={{
                                margin: '8px 0',
                                position: 'relative',
                                cursor: 'pointer',
                                padding: '4px 8px',
                                borderRadius: '4px',
                                background: hoveredIndex === idx ? '#f0f0f0' : 'transparent',
                            }}
                            onMouseEnter={() => setHoveredIndex(idx)}
                            onMouseLeave={() => setHoveredIndex(null)}
                        >
                            {item.name}

                            {hoveredIndex === idx && (
                                <div
                                    style={{
                                        position: 'absolute',
                                        top: '100%',
                                        left: '0',
                                        marginTop: '4px',
                                        padding: '8px',
                                        background: 'white',
                                        border: '1px solid #ccc',
                                        borderRadius: '4px',
                                        boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
                                        zIndex: 10,
                                        whiteSpace: 'nowrap',
                                    }}
                                >
                                    <div style={{ fontSize: '14px', fontWeight: 'bold' }}>
                                        {label}: {value} {unit}
                                    </div>
                                    <div style={{ fontSize: '13px', color: '#555' }}>
                                        Category: {item.category} | Size: {item.size}
                                    </div>
                                    <div style={{ fontSize: '13px', color: '#00704A' }}>
                                        Factor: {item.factor}
                                    </div>
                                </div>
                            )}
                        </li>
                    );
                })}
            </ul>
        </div>
    );
}

export default RecommendedItems;
