import { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function AvgNutrientChart({ onBubbleClick, nutrient }) {
    const ref = useRef();

    useEffect(() => {
        const dummy = [
            { type: 'Espresso', avg: nutrient === 'caffeine' ? 90 : 10 },
            { type: 'Frappuccino', avg: nutrient === 'caffeine' ? 60 : 20 },
            { type: 'Tea', avg: nutrient === 'caffeine' ? 40 : 15 },
        ];

        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = 300;
        const height = 200;
        const scale = d3.scaleSqrt().domain([0, 100]).range([10, 50]);

        const simulation = d3.forceSimulation(dummy)
            .force('x', d3.forceX(width / 2).strength(0.05))
            .force('y', d3.forceY(height / 2).strength(0.05))
            .force('collide', d3.forceCollide(d => scale(d.avg) + 4))
            .stop();

        for (let i = 0; i < 100; ++i) simulation.tick();

        const nodes = svg.selectAll('g')
            .data(dummy)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x}, ${d.y})`)
            .on('click', d => onBubbleClick(d.type));

        nodes.append('circle')
            .attr('r', d => scale(d.avg))
            .attr('fill', 'orange');

        nodes.append('text')
            .attr('text-anchor', 'middle')
            .attr('dy', '.35em')
            .text(d => d.type);
    }, [nutrient]);

    return (
        <div>
            <h3>Average {nutrient.replace(/_/g, ' ')} by Type</h3>
            <svg ref={ref} width={300} height={200}></svg>
        </div>
    );
}

export default AvgNutrientChart;
