import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { formatLabel } from '../../data/DrinkNutritionModel';
import './BubbleChart.css';

function BubbleChart({ data, nutrient }) {
    const ref = useRef();

    useEffect(() => {
        if (!data || data.length === 0) return;

        // Step 1: Group drinks by category and compute averages
        const grouped = d3.groups(data, d => d.category);
        const categoryAverages = grouped.map(([category, items]) => {
            const valid = items.filter(d => d[nutrient] !== undefined && !isNaN(d[nutrient]));
            const avg = d3.mean(valid, d => +d[nutrient]);
            return { category, avg };
        }).filter(d => !isNaN(d.avg));

        // Step 2: Setup SVG
        const svg = d3.select(ref.current);
        svg.selectAll('*').remove();

        const width = svg.node().getBoundingClientRect().width;
        const height = svg.node().getBoundingClientRect().height;

        const scale = d3.scaleSqrt()
            .domain([0, d3.max(categoryAverages, d => d.avg)])
            .range([20, 60]);

        // Step 3: Force layout
        const simulation = d3.forceSimulation(categoryAverages)
            .force('x', d3.forceX(width / 2).strength(0.05))
            .force('y', d3.forceY(height / 2).strength(0.05))
            .force('collide', d3.forceCollide(d => scale(d.avg) + 4))
            .stop();

        for (let i = 0; i < 100; ++i) simulation.tick();

        // Step 4: Render bubbles
        const g = svg.selectAll('g')
            .data(categoryAverages)
            .enter()
            .append('g')
            .attr('transform', d => `translate(${d.x},${d.y})`);

        g.append('circle')
            .attr('r', d => scale(d.avg))
            .attr('fill', 'orange')
            .attr('opacity', 0.8);

        g.append('text')
            .attr('text-anchor', 'middle')
            .attr('dy', '.35em')
            .style('font-size', '12px')
            .style('fill', '#333')
            .text(d => d.category.length > 20 ? d.category.slice(0, 18) + '…' : d.category);

    }, [data, nutrient]);

    return (
        <div>
            <h3>Average {formatLabel(nutrient)} by Type of Drinks</h3>
            <svg ref={ref} style={{ width: '100%', height: '250px' }}></svg>
        </div>
    );
}

export default BubbleChart;
