import React from 'react';
import recommendations from '../../data/HealthyDrinkRecommendations.json';

function RecommendedItems({ drinkName, nutrient }) {
    if (!drinkName) return null;

    const selected = recommendations.find((d) => d.name === drinkName);
    if (!selected) return <p>Loading...</p>;

    const label = nutrient
        .replace(/_/g, ' ')
        .replace(/\b\w/g, (c) => c.toUpperCase());

    const unitMap = {
        caffeine: 'mg',
        vitaminA: '%',
        vitaminC: '%',
        calcium: '%',
        iron: '%',
        calories: '',
    };
    const unit = unitMap[nutrient] || 'g';

    const factorText = selected.factor;

    return (
        <div className="drink-info">
            <h3>Recommendation: {factorText}</h3>
            <p>
                {label}: <strong>{selected[nutrient]}</strong> {unit}
            </p>
            <p style={{ fontSize: '13px', color: '#777' }}>
                Category: <em>{selected.category}</em> | Size: {selected.size}
            </p>
            <p style={{ fontSize: '13px', color: '#00704A' }}>
                Health Factor: {factorText}
            </p>
        </div>
    );
}

export default RecommendedItems;
